import React,{useEffect, useState} from 'react';
import Modal from '../Modal';

const Articles = () => {
  const [loading , setLoading]= useState(true);

  useEffect(() => {
    setInterval(() => {
      setLoading(false)
    }, 5000);
  })

  return (<div>
      <Modal openModal={loading}/>
  </div>)
};

export default Articles;
