import React, { useState } from 'react';
import './Modal.css'

const Modal = (props) => {
  const { openModal } = props;

  if(openModal === false){
    return;
  }

  return( 
    <div id="myModal" className="modal">
      <div className="modal-content">
        <span className="close">&times;</span>
        <p>Some text in the Modal..</p>
      </div>
    </div>  
  );
};

export default Modal;
