import React,{ useEffect, useState } from 'react'; 
import Modal from './components/Modal';
import './App.css';

function App() {
  const [loading , setLoading]= useState(false);

  const handleOpenModal = () => {
    setLoading(true)
  }

  return (
    <div className="App">
      <Modal openModal={loading}/>
      <button onClick={handleOpenModal} type="button">Open Modal!</button>
    </div>
  );
}

export default App;
